import React, { Component } from 'react'
import Ingredient from './Ingredient'

export default class IngredientList extends Component {

    state = {
        ingredientArray: this.props.ingredients,
        newItem: ''
    }

    addItem = (e) => {
        let tempIngredientArray = []
        tempIngredientArray.push(this.state.newItem)
        this.setState({
            ingredientArray: tempIngredientArray,
            newItem: ''
        })
    }
    render() {
        const ingredientItems = this.props.ingredients.map((ingredient, index) => {
            return <div className="listDiv" key={index}><li key={index}>{ingredient.name}</li>
            <button onClick={(e)=> this.addItem(e)}>Add to Burger</button>
            </div>
        })
