import React, { Component } from 'react'

export default class IngredientList extends Component {

    state = {
        ingredientsToDisplay: this.props.ingredients
    }
    render() {
        const ingredientItems = this.props.ingredients.map((ingredient, index) => {
            return <div className="listDiv" key={index}><li key={index}>{ingredient.name}</li>
            <button id={index}>Add to Burger</button>
            </div>
        })
        // console.log("Ingredients in IL: ", this.props.ingredients[0].name)
        return (
            <div className="container">
                <h1>Ingredients</h1>
                <ul>{ingredientItems}</ul>
            </div>
        )
    }
}
